package ocajp.q056;

public class Test {
    //Wrapper類別的預設值都是null
    char c;
    boolean b;
    float f;
    void printAll() {
        System.out.println("c:"+c);
        System.out.println("b:"+b);
        System.out.println("f:"+f);
    }
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        Test t = new Test();
        t.printAll();
    }

}
